<script>
import Clipboard from 'clipboard'

export default {
  data () {
    return {
      copied: false
    }
  },
  mounted () {
    this.setupCopyInstall()
  },
  methods: {
    setupCopyInstall () {
      if (!this.$refs.copyInstall) {
        return this.$nextTick(this.setupCopyInstall)
      }
      const copyInstall = new Clipboard(this.$refs.copyInstall)
